	}

	// process combos
	$templateCode = str_replace('<%%COMBO(articulo)%%>', $combo_articulo->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(articulo)%%>', $combo_articulo->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(articulo)%%>', urlencode($combo_articulo->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(marca)%%>', $combo_marca->HTML, $templateCode);
