	$templateCode = str_replace('<%%UPLOADFILE(motor_modelo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(motor_serie)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
