function planificaciones_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('planificaciones');
	if(!$arrPerm[1]){
		return false;
	}

	$data['titulo'] = makeSafe($_REQUEST['titulo']);
		if($data['titulo'] == empty_lookup_value){ $data['titulo'] = ''; }
	$data['tecnico'] = makeSafe($_REQUEST['tecnico']);
		if($data['tecnico'] == empty_lookup_value){ $data['tecnico'] = ''; }
	$data['camioneta'] = makeSafe($_REQUEST['camioneta']);
		if($data['camioneta'] == empty_lookup_value){ $data['camioneta'] = ''; }
	$data['fecha_planificado'] = intval($_REQUEST['fecha_planificadoYear']) . '-' . intval($_REQUEST['fecha_planificadoMonth']) . '-' . intval($_REQUEST['fecha_planificadoDay']);
	$data['fecha_planificado'] = parseMySQLDate($data['fecha_planificado'], '');

	// hook: planificaciones_before_insert
	if(function_exists('planificaciones_before_insert')){
		$args=array();
		if(!planificaciones_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `planificaciones` set       `titulo`=' . (($data['titulo'] !== '' && $data['titulo'] !== NULL) ? "'{$data['titulo']}'" : 'NULL') . ', `tecnico`=' . (($data['tecnico'] !== '' && $data['tecnico'] !== NULL) ? "'{$data['tecnico']}'" : 'NULL') . ', `camioneta`=' . (($data['camioneta'] !== '' && $data['camioneta'] !== NULL) ? "'{$data['camioneta']}'" : 'NULL') . ', `fecha_planificado`=' . (($data['fecha_planificado'] !== '' && $data['fecha_planificado'] !== NULL) ? "'{$data['fecha_planificado']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"planificaciones_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: planificaciones_after_insert
	if(function_exists('planificaciones_after_insert')){
		$res = sql("select * from `planificaciones` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!planificaciones_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('planificaciones', $recID, getLoggedMemberID());

	return $recID;
}

