	sql('update `planificaciones` set       `titulo`=' . (($data['titulo'] !== '' && $data['titulo'] !== NULL) ? "'{$data['titulo']}'" : 'NULL') . ', `tecnico`=' . (($data['tecnico'] !== '' && $data['tecnico'] !== NULL) ? "'{$data['tecnico']}'" : 'NULL') . ', `camioneta`=' . (($data['camioneta'] !== '' && $data['camioneta'] !== NULL) ? "'{$data['camioneta']}'" : 'NULL') . ', `fecha_planificado`=' . (($data['fecha_planificado'] !== '' && $data['fecha_planificado'] !== NULL) ? "'{$data['fecha_planificado']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
