	}


	// hook: planificaciones_after_update
	if(function_exists('planificaciones_after_update')){
		$res = sql("SELECT * FROM `planificaciones` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
