		if(!$_REQUEST['Embedded']) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate){
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return planificaciones_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#titulo').replaceWith('<div class=\"form-control-static\" id=\"titulo\">' + (jQuery('#titulo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#tecnico').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#tecnico_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#camioneta').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#camioneta_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#fecha_planificado').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#fecha_planificadoDay, #fecha_planificadoMonth, #fecha_planificadoYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(tecnico)%%>', $combo_tecnico->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(tecnico)%%>', $combo_tecnico->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(tecnico)%%>', urlencode($combo_tecnico->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(camioneta)%%>', $combo_camioneta->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(camioneta)%%>', $combo_camioneta->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(camioneta)%%>', urlencode($combo_camioneta->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(fecha_planificado)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_fecha_planificado->GetHTML(true) . '</div>' : $combo_fecha_planificado->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(fecha_planificado)%%>', $combo_fecha_planificado->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'tecnico' => array('tecnicos', 'Tecnico'), 'camioneta' => array('camionetas', 'Camioneta'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(titulo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tecnico)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(camioneta)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(fecha_planificado)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(titulo)%%>', safe_html($urow['titulo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(titulo)%%>', html_attr($row['titulo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(titulo)%%>', urlencode($urow['titulo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(tecnico)%%>', safe_html($urow['tecnico']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(tecnico)%%>', html_attr($row['tecnico']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tecnico)%%>', urlencode($urow['tecnico']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(camioneta)%%>', safe_html($urow['camioneta']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(camioneta)%%>', html_attr($row['camioneta']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(camioneta)%%>', urlencode($urow['camioneta']), $templateCode);
		$templateCode = str_replace('<%%VALUE(fecha_planificado)%%>', @date('m/d/Y', @strtotime(html_attr($row['fecha_planificado']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fecha_planificado)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['fecha_planificado'])))), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(titulo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(titulo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tecnico)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tecnico)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(camioneta)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(camioneta)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(fecha_planificado)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fecha_planificado)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('planificaciones');
	if($selected_id){
		$jdata = get_joined_record('planificaciones', $selected_id);
		if($jdata === false) $jdata = get_defaults('planificaciones');
		$rdata = $row;
	}
	$templateCode .= loadView('planificaciones-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: planificaciones_dv
	if(function_exists('planificaciones_dv')){
		$args=array();
		planificaciones_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>